long_json_str = '{"id": "' + long_str + '"}'

# testing increment
inc = 100


# generate varying test string lenghts
def chop_str(long_str, inc = 10):
    n = 0
    while len(long_str) > n*inc:
        test_str = long_str[:-(n*inc+1)]
        test_json_str = '{"id": "' + test_str + '"}'
        test_json = json.loads(test_json_str)

        n += 1

        yield len(test_json_str), test_json


def run_test(ser, compression):
    with Radio(serialcon=ser,
               max_chunk_size=4096,
               compression=compression) as radio:

        # x-axis
        x = []

        # y-axis
        y = []

        for length, test_json in chop_str(long_str, inc):
            x.append(length)

            time_start = time.time()

            future_send = radio.send(test_json, b'00011')
            future_send.result()

            time_end = time.time()

            delta_time = time_end - time_start

            y.append(delta_time)

        return x, y


# load serial configuration from json formatted file
with open("radio_settings.json", "r") as fpointer:
    config = json.load(fpointer)

ser = serial.Serial(config["serial"]["port"])  # as str in radio_settings.json
ser.baudrate = config["serial"]["baudrate"]  # as int in radio_settings.json
ser.parity = eval(config["serial"]["parity"])  # we have to evaluate the code in the file
ser.stopbits = eval(config["serial"]["stopbits"])
ser.bytesize = eval(config["serial"]["bytesize"])


# baseline without compression
x_base, y_base = run_test(ser, False)

# test with compression enabled
x_comp, y_comp = run_test(ser, True)

